mapper.select:
SELECT ifnull(sum(data), 0)
FROM data_expenses
WHERE order_id = #{orderId}
  and is_local = 1;

SELECT ifnull(sum(data), 0)
FROM data_expenses
WHERE order_id = #{orderId}
  and is_local = 0;

SELECT ifnull(sum(data), 0)
FROM data_expenses
WHERE user_id = #{userId}
  and order_id is null
  and is_local = 1
  and time between #{startTime} and #{endTime};

SELECT ifnull(sum(data), 0)
FROM data_expenses
WHERE user_id = #{userId}
  and order_id is null
  and is_local = 0
  and time between #{startTime} and #{endTime};
